import { createUseStyles } from "react-jss";
import { Pokemon } from "../../hooks/useGetPokemons";

interface Props {
    pkmnDetail: Pokemon;
}

const PokemonDetail = ({pkmnDetail}: Props) => {
    const classes = useStyles();

    return <tr className={classes.pokemon}><td>{pkmnDetail.name}</td><td>{pkmnDetail.number}</td><td>{pkmnDetail.types.join()}</td><td><img src={pkmnDetail.image}/></td></tr>
}

const useStyles = createUseStyles(
    {
      pokemon: {
        cursor: 'pointer',
      }
    },
    { name: 'PokemonDetail' }
  );

export default PokemonDetail;
